import matplotlib.pyplot as plt
import numpy as np
from matplotlib import rcParams

def draw(acc,loss,title,xlabel,ylabel,out_path='output.png',label_1='准确率',label_2='损失值'):
    rcParams['font.family'] = 'SimHei'
    # 创建数据
    x = [i for i in range(len(loss))]
    # 画折线图
    plt.plot(x,acc, linewidth=2, color='blue',label=label_1)
    plt.plot(x,loss, linewidth=2, color='green',label=label_2)
    # 设置标题和轴标签
    plt.title(title, fontsize=16)
    plt.xlabel(xlabel, fontsize=14)
    plt.ylabel(ylabel, fontsize=14)
    plt.legend()
    # # 设置轴的标尺
    # 显示图形
    plt.savefig(out_path) #这个要在show之前
    plt.show()


